import React from "react";
import s from './Users.module.css'
import {AxiosInstance as axios} from "axios";

function Users(props) {

    if(props.users.length === 0){

        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response =>{
            props.setUsers()
        })

        props.setUsers([
            {
                id: 1,
                photoUrl: 'https://img2.pngio.com/avatar-ninja-samurai-warrior-icon-ninja-avatar-png-512_512.png',
                followed: true,
                fullName: 'Dmitry',
                status: 'Im a boss',
                location: {city: 'Minsk', country: 'Belarus'}
            },
            {
                id: 2,
                photoUrl: 'https://img2.pngio.com/avatar-ninja-samurai-warrior-icon-ninja-avatar-png-512_512.png',
                followed: false,
                fullName: 'Zhenya',
                status: 'Happy now',
                location: {city: 'Kiev', country: 'Ukraine'}
            },
            {
                id: 3,
                photoUrl:'https://img2.pngio.com/avatar-ninja-samurai-warrior-icon-ninja-avatar-png-512_512.png',
                followed: false,
                fullName: 'Petro',
                status: 'This is me!',
                location: {city: 'New York', country: 'USA'}
            },
            {
                id: 4,
                photoUrl:'https://img2.pngio.com/avatar-ninja-samurai-warrior-icon-ninja-avatar-png-512_512.png',
                followed: false,
                fullName: 'Bill',
                status: 'i have never seen this car before!',
                location: {city: 'Vinnitsa', country: 'Ukraine'}
            },
            {
                id: 5,
                photoUrl:'https://img2.pngio.com/avatar-ninja-samurai-warrior-icon-ninja-avatar-png-512_512.png',
                followed: true,
                fullName: 'Alex',
                status: 'This is my first job',
                location: {city: 'Krakow', country: 'Poland'}
            },
        ])
    }

    return (<div>
        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div className={`${s.img}`}>
                        <img src={u.photoUrl}/>
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={()=>{props.unfollow(u.id)}}>Unfollow</button>
                            : <button onClick={()=>{props.follow(u.id)}}>Follow</button>}

                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.fullName}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{u.location.country}</div>
                        <div>{u.location.city}</div>
                    </span>
                </span>
            </div>)
        }
    </div>)

}

export default Users;
