import React from "react";
import s from "./Users.module.css";
import userPhoto from "../../assets/images/149071.png";
import * as axios from "axios";

class UsersC extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/
        users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(response => {
                // console.log(response.data.items)// response data
                this.props.setUsers(response.data.items)
            });
    }


    render() {
        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }


        return (
            <div>
                <div>
                    {pages.map(p => {
                        return <span className={this.props.currentPage === p && s.selectedPage}>{p}</span>
                    })}

                </div>
                {
                    this.props.users.map(u => <div key={u.id}>
                <span>
                    <div className={`${s.img}`}>
                        <img src={u.photos.small != null ? u.photos.small : userPhoto}/>
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                this.props.unfollow(u.id)
                            }}>Unfollow</button>
                            : <button onClick={() => {
                                this.props.follow(u.id)
                            }}>Follow</button>}

                    </div>
                </span>
                        <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{"u.location.country"}</div>
                        <div>{"u.location.city"}</div>
                    </span>
                </span>
                    </div>)
                }
            </div>)
    }
}

export default UsersC;
