const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET_USERS'

let initialState = {
    users: [
        {
            id: 1,
            followed: true,
            fullName: 'Dmitry',
            status: 'Im a boss',
            location: {city: 'Minsk', country: 'Belarus'}
        },
        {id: 2, followed: false, fullName: 'Zhenya', status: 'Happy now', location: {city: 'Kiev', country: 'Ukraine'}},
        {
            id: 3,
            followed: false,
            fullName: 'Petro',
            status: 'This is me!',
            location: {city: 'New York', country: 'USA'}
        },
        {
            id: 4,
            followed: false,
            fullName: 'Bill',
            status: 'i have never seen this car before!',
            location: {city: 'Vinnitsa', country: 'Ukraine'}
        },
        {
            id: 5,
            followed: true,
            fullName: 'Alex',
            status: 'This is my first job',
            location: {city: 'Krakow', country: 'Poland'}
        },
    ]
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u
                })
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u
                })
            }
        case SET_USERS:
            return {...state, users: [...state.users, ...action.users]}
        default:
            return state;
    }
}

export const followAC = (userId) => ({type: FOLLOW, userId})
export const unFollowAC = (userId) => ({type: UNFOLLOW, userId})
export const setUsersAC = (users) => ({type: SET_USERS, users})


export default usersReducer;
