import dialogsReducer from "./dialogs-reducer";
import profileReducer from "./profile-reducer";

const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';

const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT'
const SEND_MESSAGE = 'SEND-MESSAGE'

let store = {
    _state: {
        profilePage: {
            postsData: [
                {id: 1, message: 'Hi, how are you?', likesCount: 15},
                {id: 2, message: 'This is my second post', likesCount: 23},
                {id: 3, message: 'Hi everyone!', likesCount: 67}],
            newPostText: ''
        },
        messagePage: {
            dialogsData: [
                {id: 1, name: 'Dima'},
                {id: 2, name: 'Andrey'},
                {id: 3, name: 'Sveta'},
                {id: 4, name: 'Anna'},
                {id: 5, name: 'Petro'}],
            messageData: [
                {id: 1, message: 'hi'},
                {id: 2, message: 'yo'},
                {id: 3, message: 'How are you?'},
                {id: 4, message: 'Do you know me?'},
                {id: 5, message: 'By by'}],
            newMessageBody:''
        }
    },
    _callSubscriber() {
        console.log("State changed");
    },
    getState() {
        return this._state;
    },
    subscribe(observer) {
        this._callSubscriber = observer;// патерн observer
    },

    // або через приват методи визиваючи їх в dispatch

    dispatch(action) {
        this._state.profilePage = profileReducer(this._state.profilePage,action)
        this._state.messagePage = dialogsReducer(this._state.messagePage,action)

        this._callSubscriber(this._state)

    }
}

export const sendMessageCreator = () => {
    return{
        type: SEND_MESSAGE
    }
}
export const updateNewMessageBodyCreator = (body) =>{
    return{
        type:UPDATE_NEW_MESSAGE_TEXT,
        body:body
    }
}

// export const addPostActionCreator =() => ({type:ADD_POST})
// or

export const addPostActionCreator = () => {
    return {
        type: ADD_POST
    }
}


export const updateNewPostTextActionCreator = (text) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        newText: text
    }
}


export default store;
